body.script:
//

// --- Reading Progress Bar ---
window.addEventListener('scroll', function() {
  var winScroll = document.body.scrollTop || document.documentElement.scrollTop;
  var height = document.documentElement.scrollHeight - document.documentElement.clientHeight;
  var scrolled = (winScroll / height) * 100;
  var bar = document.getElementById('reading-progress');
  if (bar) bar.style.width = scrolled + '%';

  // Back to top button
  var btn = document.getElementById('backToTop');
  if (btn) {
    if (winScroll > 400) {
      btn.classList.add('visible');
    } else {
      btn.classList.remove('visible');
    }
  }
});

// --- Auto Table of Contents ---
(function() {
  var articleBody = document.querySelector('.article-body');
  if (!articleBody) return;

  var headings = articleBody.querySelectorAll('h2, h3');
  if (headings.length < 2) return;

  var tocContainer = document.getElementById('toc-container');
  var tocList = document.getElementById('toc-list');
  if (!tocContainer || !tocList) return;

  tocContainer.classList.add('active');

  headings.forEach(function(heading, i) {
    var id = 'section-' + i;
    heading.id = id;

    var li = document.createElement('li');
    var a = document.createElement('a');
    a.href = '#' + id;
    a.textContent = heading.textContent;
    a.onclick = function(e) {
      e.preventDefault();
      heading.scrollIntoView({ behavior: 'smooth', block: 'start' });
    };

    if (heading.tagName === 'H3') {
      a.style.paddingLeft = '24px';
      a.style.fontSize = '12px';
    }

    li.appendChild(a);
    tocList.appendChild(li);
  });

  // Active heading highlight on scroll
  window.addEventListener('scroll', function() {
    var scrollPos = window.scrollY + 100;
    var links = tocList.querySelectorAll('a');

    headings.forEach(function(heading, i) {
      var nextTop = (i + 1 < headings.length) ? headings[i + 1].offsetTop : document.body.scrollHeight;
      if (scrollPos >= heading.offsetTop && scrollPos < nextTop) {
        links.forEach(function(l) { l.classList.remove('active'); });
        links[i].classList.add('active');
      }
    });
  });
})();

// --- Style references section ---
(function() {
  var body = document.querySelector('.article-body');
  if (!body) return;

  // Find and style the references section
  var allElements = body.querySelectorAll('h2, h3, h4, p, strong');
  allElements.forEach(function(el) {
    if (el.textContent.indexOf('참고자료') !== -1 || el.textContent.indexOf('[참고자료]') !== -1) {
      var container = document.createElement('div');
      container.className = 'references';

      var sibling = el;
      var toWrap = [el];
      while (sibling.nextElementSibling) {
        sibling = sibling.nextElementSibling;
        if (sibling.tagName === 'HR') break;
        toWrap.push(sibling);
      }

      if (toWrap.length > 1) {
        el.parentNode.insertBefore(container, el);
        toWrap.forEach(function(node) { container.appendChild(node); });
      }
    }
  });

  // Find and style disclaimer
  var paras = body.querySelectorAll('p, strong');
  paras.forEach(function(el) {
    if (el.textContent.indexOf('면책 고지') !== -1 || el.textContent.indexOf('면책고지') !== -1) {
      var wrap = el.closest('p') || el;
      var disclaimer = document.createElement('div');
      disclaimer.className = 'disclaimer';
      disclaimer.innerHTML = wrap.innerHTML;
      wrap.parentNode.replaceChild(disclaimer, wrap);
    }
  });
})();

